def.body:
old_rate = @tick_rate
old_frozen = @ticking_frozen

@tick_rate = new_tick_rate
@ticking_frozen = frozen

Log.debug do "Ticking state updated: rate #{old_rate} -> #{new_tick_rate}, frozen #{old_frozen} -> #{frozen}" end

@paused = true
@movement_action.try(&.fail(Client::NotConnected.new("Disconnected from server")))
@movement_action = nil
@look_action.try(&.fail(Rosegold::Client::NotConnected.new("Disconnected from server")))
@look_action = nil

spawn_chunk_x = player.feet.x.to_i >> 4
spawn_chunk_z = player.feet.z.to_i >> 4
spawn_chunk_loaded = client.dimension.chunks.has_key?({spawn_chunk_x, spawn_chunk_z})

if spawn_chunk_loaded
  @paused = false
  Log.debug do "Physics unpaused - spawn chunk (#{spawn_chunk_x}, #{spawn_chunk_z}) is loaded" end
else
  @paused = true
  Log.debug do "Physics remains paused - waiting for spawn chunk (#{spawn_chunk_x}, #{spawn_chunk_z}) to load" end
end

player.velocity = Vec3d::ORIGIN

@last_sent_feet = player.feet
@last_sent_look = player.look
@last_sent_on_ground = player.on_ground?
@ticks_since_last_packet = 0

@cached_item || (
  @cached_item = MCData::DEFAULT.items.find do |item| item.id == item_id_int end ||
  (raise("Unknown item ID: #{item_id_int}"))
)

if closed?
  return
end
self.closed = true

if @client.window == self
  @client.inventory.previous_window_id = self.id
  @client.window = @client.inventory
  @client.inventory.slots = ((@client.inventory.slots[0..8] + inventory) + hotbar) + @client.inventory.slots[45..45]
  @client.inventory.slots.each_with_index do |slot, i| slot.slot_number = i end
end
