def.body:
old_rate = @tick_rate
old_frozen = @ticking_frozen

@tick_rate = new_tick_rate
@ticking_frozen = frozen

Log.debug do "Ticking state updated: rate #{old_rate} -> #{new_tick_rate}, frozen #{old_frozen} -> #{frozen}" end

case index
when SlotOffsets::PlayerSlots::CRAFTING_RESULT
  @crafting_result
when SlotOffsets::PlayerSlots::CRAFTING_START..SlotOffsets::PlayerSlots::CRAFTING_END
  @crafting_slots[index - SlotOffsets::PlayerSlots::CRAFTING_START]
when SlotOffsets::PlayerSlots::ARMOR_START..SlotOffsets::PlayerSlots::ARMOR_END
  @armor_slots[index - SlotOffsets::PlayerSlots::ARMOR_START]
when SlotOffsets::PlayerSlots::MAIN_START..SlotOffsets::PlayerSlots::MAIN_END
  @player_inventory[index]
when SlotOffsets::PlayerSlots::HOTBAR_START..SlotOffsets::PlayerSlots::HOTBAR_END
  @player_inventory[index - SlotOffsets::PlayerSlots::HOTBAR_START]
when SlotOffsets::PlayerSlots::OFF_HAND
  @off_hand
else
  raise(ArgumentError.new("Invalid slot index #{index} for player inventory menu"))
end

case index
when SlotOffsets::PlayerSlots::CRAFTING_RESULT
  @crafting_result = slot
when SlotOffsets::PlayerSlots::CRAFTING_START..SlotOffsets::PlayerSlots::CRAFTING_END
  @crafting_slots[index - SlotOffsets::PlayerSlots::CRAFTING_START] = slot
when SlotOffsets::PlayerSlots::ARMOR_START..SlotOffsets::PlayerSlots::ARMOR_END
  @armor_slots[index - SlotOffsets::PlayerSlots::ARMOR_START] = slot
when SlotOffsets::PlayerSlots::MAIN_START..SlotOffsets::PlayerSlots::MAIN_END
  @player_inventory[index] = slot
when SlotOffsets::PlayerSlots::HOTBAR_START..SlotOffsets::PlayerSlots::HOTBAR_END
  @player_inventory[index - SlotOffsets::PlayerSlots::HOTBAR_START] = slot
when SlotOffsets::PlayerSlots::OFF_HAND
  @off_hand = slot
else
  raise(ArgumentError.new("Invalid slot index #{index} for player inventory menu"))
end

if @cursor.empty?
  return
end

if button == 0
  @cursor = Rosegold::Slot.new
elsif @cursor.count > 1
  @cursor.count = @cursor.count - 1
else
  @cursor = Rosegold::Slot.new
end

@state_id = packet_state_id

slots.each_with_index do |slot, index|
  if index < total_slots
    self[index] = slot
  end
end

@cursor = cursor


slots.each_with_index do |slot, index|
  if index < @remote_slots.size
    @remote_slots[index].force(slot)
  end
end
@remote_cursor.force(cursor)

@state_id = packet_state_id

if index >= 0 && index < total_slots
  self[index] = slot

  if index < @remote_slots.size
    @remote_slots[index].force(slot)
  end
elsif index == -1
  @cursor = slot
  @remote_cursor.force(slot)
end

if helmet?(item_id)
  return PlayerSlots::HELMET_SLOT
end
if chestplate?(item_id)
  return PlayerSlots::CHESTPLATE_SLOT
end
if leggings?(item_id)
  return PlayerSlots::LEGGINGS_SLOT
end
if boots?(item_id)
  return PlayerSlots::BOOTS_SLOT
end
nil

@paused = true
@movement_action.try(&.fail(Client::NotConnected.new("Disconnected from server")))
@movement_action = nil
@look_action.try(&.fail(Rosegold::Client::NotConnected.new("Disconnected from server")))
@look_action = nil

spawn_chunk_x = player.feet.x.to_i >> 4
spawn_chunk_z = player.feet.z.to_i >> 4
spawn_chunk_loaded = client.dimension.chunks.has_key?({spawn_chunk_x, spawn_chunk_z})

if spawn_chunk_loaded
  @paused = false
  Log.debug do "Physics unpaused - spawn chunk (#{spawn_chunk_x}, #{spawn_chunk_z}) is loaded" end
else
  @paused = true
  Log.debug do "Physics remains paused - waiting for spawn chunk (#{spawn_chunk_x}, #{spawn_chunk_z}) to load" end
end

player.velocity = Vec3d::ORIGIN
player.on_ground = false

@last_sent_feet = player.feet
@last_sent_on_ground = player.on_ground?
@ticks_since_last_packet = 0
@last_sent_input_flags = Serverbound::PlayerInput::Flag::None

if index >= 0 && index < INVENTORY_SIZE
  @items[index] = slot
end

previous_state = @blocks[index]
@blocks[index] = block_state


if previous_state != 0_u16
  @block_count = @block_count - 1
end
if block_state != 0_u16
  @block_count = @block_count + 1
end

previous_state

@cached_item || (
  @cached_item = MCData::DEFAULT.items.find do |item| item.id == item_id_int end ||
  (raise("Unknown item ID: #{item_id_int}"))
)
